import React, { useState } from 'react';
import Resizer from 'react-image-file-resizer';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { toast } from 'react-toastify';

import './index.css';

const FileUpload = ({ id, images, setImages }) => {
    const { user } = useSelector((state) => ({ ...state }));

    const [loading, setLoading] = useState(false);

    const fileUploadAndResize = (e) => {
        e.preventDefault();
        let files = e.target.files; // 3
        let allUploadedFiles = (images && images.urls) || [];
        let idToken = user ? user.token : '';

        if (files) {
            setLoading(true);
            for (let i = 0; i < files.length; i++) {
                Resizer.imageFileResizer(
                    files[i],
                    720,
                    720,
                    'JPEG',
                    100,
                    0,
                    (uri) => {
                        axios
                            .post(
                                `${process.env.REACT_APP_API}/uploadimages`,
                                { image: uri },
                                {
                                    headers: {
                                        idToken
                                    },
                                }
                            )
                            .then((res) => {
                                if (res.status === 200) {
                                    const { public_id, url, message } = res.data;
                                    toast.success(message);
                                    allUploadedFiles.push({ public_id, url });
                                    setImages({ ...images, urls: allUploadedFiles });
                                    // localStorage.setItem('uploadedImages', { ...images, urls: allUploadedFiles });
                                } else {
                                    toast.error(res.data.message);
                                }
                                setLoading(false);
                            })
                            .catch((error) => {
                                setLoading(false);
                                toast.error(error);
                            });
                    },
                    'base64'
                );
            }
        }
        // send back to server to upload to cloudinary
        // set url to images[] in the parent component state - ProductCreate
    };

    return (
        <>
            {signUp ? (
                <>
                    <label htmlFor={id} className='btn btn-filled btn-raised'>
                        Choose File
                        <input
                            id={id}
                            type='file'
                            multiple
                            hidden
                            accept='images/*'
                            onChange={fileUploadAndResize}
                        />
                    </label>
                    <div className='mt-3'>
                        {images && images.urls &&
                            images.urls.map((image) => (
                                <>
                                    <Badge
                                        count='X'
                                        key={image.public_id}
                                        onClick={() => handleImageRemove(image.public_id)}
                                        style={{ cursor: 'pointer' }}
                                    >
                                        <Avatar
                                            src={image.url}
                                            size={100}
                                            shape='square'
                                            className='ml-3'
                                        />
                                    </Badge>
                                </>
                            ))}
                    </div>
                </>
            ) : <input
                id={id}
                type='file'
                multiple
                hidden
                accept='images/*'
                onChange={fileUploadAndResize}
            />}

        </>
    );
};

export default FileUpload;
