import { toast } from 'react-toastify';

import { createOrUpdateCart } from '../../Functions/Auth';

const customHook = ({
    setLoading,
    restaurant,
    user,
    cart,
    items,
    setItems,
    dispatch,
    navigate
}) => {
    let itemsArr = [];
    let cuisinesArr = [];
    let index = -1;
    let itemIndex = -1;

    const handleAddProduct = (obj) => {
        setLoading(true);
        if (user) {
            const { product, count } = obj;
            let arr = {
                cuisineId: product._id,
                title: product.title,
                description: product.description,
                price: product.price,
                count: count,
                quantity: product.quantity,
                images: product.images,
            };
            itemsArr = cart?.items || items;
            itemIndex = itemsArr?.findIndex(item => item.orderFrom === restaurant?.email);
            if (itemIndex !== -1) {
                cuisinesArr = itemsArr[itemIndex]?.cuisines;
                index = cuisinesArr && cuisinesArr.findIndex(item => item.cuisineId === arr.cuisineId);
                if (index !== -1) {
                    if (cuisinesArr[index].count === Number(arr.count)) {
                        toast.error('Product exist in the cart with same count');
                        setLoading(false);
                    } else {
                        cuisinesArr[index].count = arr.count;
                        let dummyArr = {
                            cuisines: cuisinesArr,
                            orderFrom: restaurant?.email
                        }
                        itemsArr[itemIndex] = dummyArr;
                        handleCommon();
                    }
                } else {
                    cuisinesArr.push(arr);
                    let dummyArr = {
                        cuisines: cuisinesArr,
                        orderFrom: restaurant?.email
                    }
                    itemsArr[itemIndex] = dummyArr;
                    handleCommon();
                }
            } else {
                cuisinesArr = [];
                cuisinesArr.push(arr);
                let dummyArr = {
                    cuisines: cuisinesArr,
                    orderFrom: restaurant?.email
                }
                itemsArr.push(dummyArr)
                handleCommon();
            }
        } else {
            setLoading(false);
            toast.error('Please login/resgister to add items to the cart');
            navigate('/login');
        }
    }

    const handleCommon = () => {
        let cartTotal = 0;
        let cartQuantity = 0;
        for (let i = 0; i < cuisinesArr.length; i++) {
            cartTotal = cartTotal + cuisinesArr[i].price * cuisinesArr[i].count;
            cartQuantity = cartQuantity + cuisinesArr[i].count;
        }
        let shipping = (cartTotal * 0.05).toFixed(2);
        createOrUpdateCart({ items: itemsArr, shipping, cartTotal, cartQuantity, totalAfterDiscount: 100, orderdBy: user.email }, user.token)
            .then(res => {
                if (res.status === 200) {
                    setItems(res.data.cart.items);
                    dispatch({
                        type: 'CART',
                        payload: res.data.cart
                    });
                    toast.success(res.data.message);
                } else {
                    toast.error(res.data.message);
                }
                setLoading(false);
            })
            .catch((error) => {
                setLoading(false);
                toast.error(error);
            })
    }

    return ({
        handleAddProduct
    })

}

export default customHook;
