import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { toast } from 'react-toastify';

import { getRestaurant } from '../../Functions/Auth';

const RestaurantHome = () => {
    const location = useLocation();
    const [loading, setLoading] = useState(false);
    const [cuisines, setCuisines] = useState('');

    const handleRoute = (option) => {
        let routes = option.split('-');
        return `${routes.length > 1 ? routes.map(route => route.toLowerCase()).join(' ') : routes[0][0].toUowerCase().concat(routes[0].slice(1))}`;
    }

    useEffect(() => {
        setLoading(true);
        getRestaurant(handleRoute(location.pathname.split('/')[2]))
            .then(res => {
                if (res.status === 200) {
                    setCuisines(res.data.restaurant);
                    toast.success(res.data.message);
                } else {
                    toast.error(res.data.message)
                }
                setLoading(false);
            })
            .catch(error => {
                toast.error(error);
                setLoading(false);
            })
    }, [])

    return(
        <></>
    )
}

export default RestaurantHome;
