import { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

import './index.css'

const SideNav = () => {

    const location = useLocation();

    let routesArr = ['/partner-signup', '/partner-signup/restaurant-type', '/partner-signup/upload-images'];

    const findBorder = val => {
        let index = routesArr.findIndex(r => r === location.pathname);
        if (val === index) {
            return 'border-left';
        } else {
            return 'border-0'
        }
    }

    return (
        <div className='col-lg-2 offset-2 col-md-8 col-10 me-2 p-0 side-nav'>
            <div className='shadow'>
                <h6 className='text-start border-bottom p-3'>1. Create your restaurant page</h6>
                <ul class='list-group'>
                    <li class={`list-group-item my-1 mx-0 px-3 py-0 ${findBorder(0)}`}>
                        <h6 className='text-start'>Restaurant Information</h6>
                        <p className='fw-light text-start'>Restaurant name, address, contact no., owner details</p>
                    </li>
                    <li class={`list-group-item my-1 mx-0 px-3 py-0 ${findBorder(1)}`}>
                        <h6 className='text-start'>Restaurant Type & Timings</h6>
                        <p className='fw-light text-start'>Establishment & cuisine type, opening hours</p>
                    </li>
                    <li class={`list-group-item my-1 mx-0 px-3 py-0 ${findBorder(2)}`}>
                        <h6 className='text-start'>Upload Images</h6>
                        <p className='fw-light text-start'>Menu, restaurant, food images</p>
                    </li>
                </ul>
            </div>
            <div className='shadow my-3'>
                <h6 className='text-start p-3'>2. Register for Online ordering</h6>
            </div>
        </div>
    )
}

export default SideNav;
