import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

import ProductCheckoutCard from '../../Components/Cards/ProductCheckoutCard';
import { createOrUpdateCart, createOrder } from '../../Functions/Auth';
import customHook from './common';

const Cart = () => {
    const { cart, user } = useSelector(state => ({ ...state }));
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [products, setProducts] = useState('');
    const [loading, setLoading] = useState(false);
    const [items, setItems] = useState('')
    let arr = [];
    let filteredItems = '';

    const { handleAddProduct } = customHook({
        setLoading,
        restaurant,
        user,
        cart,
        items,
        setItems,
        dispatch,
        navigate
    });

    useEffect(() => {
        if (cart) {
            cart.items.map(item => {
                item?.cuisines?.map(cuisine => {
                    cuisine.email = item.orderFrom
                    arr.push(cuisine)
                })
            })
            console.log('Check-->arr: ', arr);
            setProducts(arr);
        } else {
            setProducts('');
        }
    }, [cart])

    const deleteProductFromCart = product => {
        // setLoading(true);
        // items = cart && cart.items;
        // filteredItems = items.filter(item => item._id !== product._id);
        // let cartTotal = 0;
        // let cartQuantity = 0;
        // for (let i = 0; i < filteredItems.length; i++) {
        //     cartTotal = cartTotal + filteredItems[i].price * filteredItems[i].count;
        //     cartQuantity = cartQuantity + filteredItems[i].count;
        // }
        // let shipping = (cartTotal * 0.05).toFixed(2);
        // createOrUpdateCart({ items: filteredItems, shipping, cartTotal, cartQuantity, totalAfterDiscount: 100, orderdBy: user.email }, user.token)
        //     .then(res => {
        //         if (res.status === 200) {
        //             items = res.data.cart.products;
        //             dispatch({
        //                 type: 'CART',
        //                 payload: res.data.cart
        //             });
        //             toast.success(res.data.message);
        //         } else {
        //             toast.error(res.data.message);
        //         }
        //         setLoading(false);
        //     })
        //     .catch((error) => {
        //         setLoading(false);
        //         toast.error(error);
        //     })
    }

    // const updateQuantity = (obj) => {
    //     const { product, count } = obj;
    //     let cartTotal = 0;
    //     let cartQuantity = 0;
    //     let items = cart && cart.items;
    //     let index = items && items.findIndex(item => item._id === product._id);
    //     items[index].count = count;
    //     for (let i = 0; i < items.length; i++) {
    //         cartTotal = cartTotal + items[i].price * items[i].count;
    //         cartQuantity = cartQuantity + items[i].count;
    //     }
    //     let shipping = (cartTotal * 0.05).toFixed(2);
    //     createOrUpdateCart({ items, shipping, cartTotal, cartQuantity, totalAfterDiscount: 100, orderdBy: user.email }, user.token)
    //         .then(res => {
    //             if (res.status === 200) {
    //                 items = res.data.cart.items;
    //                 dispatch({
    //                     type: 'CART',
    //                     payload: res.data.cart
    //                 });
    //                 toast.success(res.data.message);
    //             } else {
    //                 toast.error(res.data.message);
    //             }
    //             setLoading(false);
    //         })
    //         .catch((error) => {
    //             setLoading(false);
    //             toast.error(error);
    //         })
    // }

    const handleCheckout = () => {
        setLoading(true);
        const { products, orderdBy, cartTotal, shipping } = cart;
        createOrder({ products, paymentIntent: {}, shipping, orderTotal: cartTotal, totalAfterDiscount: cart?.totalAfterDiscount, orderdBy }, user.token)
            .then(res => {
                if (res.status === 200) {
                    toast.success(res.data.message);
                } else {
                    toast.error(res.data.message);
                }
                setLoading(false);
            })
            .catch((error) => {
                setLoading(false);
                toast.error(error);
            })
        createOrUpdateCart({ items: [], shipping, cartTotal: 0, cartQuantity: 0, totalAfterDiscount: 0, orderdBy: user.email }, user.token)
            .then(res => {
                if (res.status === 200) {
                    setProducts('');
                    dispatch({
                        type: 'CART',
                        payload: res.data.cart
                    });
                    toast.success(res.data.message);
                } else {
                    toast.error(res.data.message);
                }
                setLoading(false);
            })
            .catch((error) => {
                setLoading(false);
                toast.error(error);
            })
    }

    return (
        <>
            {products && products.length ? <div className='container-fluid row my-5 p-0 m-0 justify-content-center'>
                <div className='col-10 col-md-7 mt-5 mx-md-2 p-3 shadow'>
                    {loading ? <h3>Loading...</h3> :
                        <div className='container-fluid mt-3'>
                            <h5 className='text-start'>Items</h5>
                            <div className='row'>
                                {products && products.map(prod =>
                                    <ProductCheckoutCard
                                        key={prod._id}
                                        product={prod}
