import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom'

const ManageRestaurants = () => {
    const { restaurants } = useSelector(state => ({ ...state }));
    const navigate = useNavigate();
    const [filteredRestaurants, setFilterRestaurants] = useState('');

    useEffect(() => {
        setFilterRestaurants(restaurants && restaurants.filter(restaurant => restaurant.status === 'accepted'));
    }, [restaurants])

    const handleRoute = (id) => {
        navigate(`/admin-restaurant-view/${id}`);
    }

    return (
        <div className='my-5 px-md-5'>
            <div className='row mt-5 pt-5 mx-md-2'>
                <div className='col-lg-8 col-10 offset-lg-2 offset-1 p-md-4 p-3 text-center shadow'>
                    <h3 className='text-center'>Manage Restaurants</h3>
                    <div className='row my-4'>
                        {filteredRestaurants?.length > 0 ? filteredRestaurants.map(restaurant =>
                            <form onSubmit={() => handleRoute(restaurant?._id)} className='my-2'>
                                <div className='row card text-center p-4'>
                                        <div className='col-lg-4 col-12 text-start'>
                                            <h4 className='text-start'>{restaurant?.restaurantName}</h4>
                                            <label className='col-form-label text-start fw-bold fs-6'>Contact:</label>
                                            <span className='ps-2'>
                                                {restaurant?.contact}
                                            </span>
                                        </div>
                                        <div className='col-lg-6 col-12 text-start'>
                                            <li className='list-group-item border-0 bg-transparent'>
                                                <label className='col-form-label text-start fw-bold fs-6'>Email:</label>
                                                <span className='ps-2'>
                                                    {restaurant?.email}
                                                </span>
                                            </li>
                                            <li className='list-group-item border-0 bg-transparent'>
                                                <label className='col-form-label text-start fw-bold fs-6'>Address:</label>
                                                <span className='ps-2'>
                                                    {restaurant?.address}, {restaurant?.city}, {restaurant?.state}, {restaurant?.zipCode}
                                                </span>
                                            </li>
                                        </div>
                                        <div className='col-lg-2 col-12 text-start d-flex align-items-center'>
                                            <button className='btn btn-filled' type='submit'>View</button>
                                        </div>
                                    </div>
                            </form>) : <h6 className='text-center'>No Restaurants Registered</h6>}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ManageRestaurants;
