import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { toast } from 'react-toastify';

import { getRestaurant, createOrUpdateCart } from '../../Functions/Auth';
import CuisineModel from '../../Components/Cards/CuisineModal';

const RestaurantHome = () => {
    const { user, cart } = useSelector((state) => ({ ...state }));
    const location = useLocation();
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [loading, setLoading] = useState(false);
    const [restaurant, setRestaurant] = useState('');
    const [cuisines, setCuisines] = useState('');
    const [items, setItems] = useState([]);

    let itemsArr = [];
    let cuisinesArr = [];
    let index = -1;
    let itemIndex = -1;

    const handleRoute = (option) => {
        let routes = option.split('-');
        return `${routes.length > 1 ? routes.map(route => route[0].toUpperCase().concat(route.slice(1))).join(' ') : routes[0][0].toUpperCase().concat(routes[0].slice(1))}`;
    }

    useEffect(() => {
        setLoading(true);
        getRestaurant(handleRoute(location.pathname.split('/')[2]))
            .then(res => {
                if (res.status === 200) {
                    setRestaurant(res.data.restaurant);
                    setCuisines(res.data.cuisines);
                    toast.success(res.data.message);
                } else {
                    toast.error(res.data.message)
                }
                setLoading(false);
            })
            .catch(error => {
                toast.error(error);
                setLoading(false);
            })
    }, [])

    const handleAddProduct = (obj) => {
        setLoading(true);
        if (user) {
            const { product, count } = obj;
            let arr = {
                cuisineId: product._id,
                title: product.title,
                description: product.description,
                price: product.price,
                count: count,
                quantity: product.quantity,
                images: product.images,
            };
            itemsArr = cart?.items || items;
            itemIndex = itemsArr?.findIndex(item => item.orderFrom === restaurant?.email);
            if (itemIndex !== -1) {
                cuisinesArr = itemsArr[itemIndex]?.cuisines;
                index = cuisinesArr && cuisinesArr.findIndex(item => item.cuisineId === arr.cuisineId);
                if (index !== -1) {
                    if (cuisinesArr[index].count === Number(arr.count)) {
                        toast.error('Product exist in the cart with same count');
                        setLoading(false);
                    } else {
                        cuisinesArr[index].count = arr.count;
                        let dummyArr = {
                            cuisines: cuisinesArr,
                            orderFrom: restaurant?.email
                        }
                        itemsArr[itemIndex] = dummyArr;
                        handleCommon();
                    }
                } else {
                    cuisinesArr.push(arr);
                    let dummyArr = {
                        cuisines: cuisinesArr,
                        orderFrom: restaurant?.email
                    }
                    itemsArr[itemIndex] = dummyArr;
                    handleCommon();
                }
            } else {
                cuisinesArr = [];
                cuisinesArr.push(arr);
                let dummyArr = {
                    cuisines: cuisinesArr,
                    orderFrom: restaurant?.email
                }
                itemsArr.push(dummyArr)
                handleCommon();
            }
        } else {
            setLoading(false);
            toast.error('Please login/resgister to add items to the cart');
            navigate('/login');
        }
    }

    const handleCommon = () => {
        let cartTotal = 0;
        let cartQuantity = 0;
        for (let i = 0; i < cuisinesArr.length; i++) {
            cartTotal = cartTotal + cuisinesArr[i].price * cuisinesArr[i].count;
            cartQuantity = cartQuantity + cuisinesArr[i].count;
        }
        let shipping = (cartTotal * 0.05).toFixed(2);
        createOrUpdateCart({ items: itemsArr, shipping, cartTotal, cartQuantity, totalAfterDiscount: 100, orderdBy: user.email }, user.token)
            .then(res => {
                if (res.status === 200) {
                    setItems(res.data.cart.items);
                    dispatch({
                        type: 'CART',
                        payload: res.data.cart
                    });
                    toast.success(res.data.message);
                } else {
                    toast.error(res.data.message);
                }
                setLoading(false);
            })
            .catch((error) => {
                setLoading(false);
                toast.error(error);
            })
    }

    return (
        <div className='container-fluid my-5 px-md-5'>
            <div className='row mt-5 pt-5 mx-md-2'>
                <div className='col-lg-10 col-10 offset-lg-1 offset-1 p-md-4 p-3 text-center shadow'>
                    {loading ? <h3>Loading...</h3> : <div className='row d-flex justify-content-center'>
                        <div className='col-5'>
                            <img src={restaurant?.restaurantImages?.length > 0 && restaurant?.restaurantImages[0]?.urls[0]?.url} className='card-img' />
                        </div>
                        <div className='col-5 text-start'>
                            <ul className='list-group'>
                                <li className='list-group-item border-0 fs-3'>{restaurant?.restaurantName}</li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Establishment Type:</label>
                                    <span className='ps-2'>
                                        {restaurant?.establishmentType}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Outlet Type:</label>
                                    <span className='ps-2'>
                                        {restaurant?.outletType?.join(', ')}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Cuisines:</label>
                                    <span className='ps-2'>
                                        {restaurant?.cuisinesType?.join(', ')}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Address:</label>
                                    <span className='ps-2'>
                                        {restaurant?.address?.concat(', ', restaurant?.city)}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Time:</label>
                                    <span className='ps-2'>
                                        {restaurant?.opensAt?.concat(' - ', restaurant?.closesAt)}
                                    </span>
                                </li>
                            </ul>
                        </div>
                        <div className='col-12 border-top border-1 text-center my-3 pt-3'>
                            <h6 className='text-start'>Full Menu</h6>
                            <div className='row d-flex'>
                                {cuisines?.products?.map(cuisine =>
                                    <div key={cuisine?._id} className='col-6 my-2'>
                                        <ul className='list-group text-start d-flex flex-row border border-1'>
                                            <li className='list-group-item border-0 w-100'>
                                                <ul className='list-group'>
                                                    <li className='list-group-item border-0 fs-6 fw-bold'>
                                                        {cuisine.title}
                                                    </li>
                                                    <li className='list-group-item border-0'>
                                                        {cuisine.description}
                                                    </li>
                                                    <li className='list-group-item border-0'>
                                                        &#36;{cuisine.price}
                                                    </li>
                                                </ul>
                                            </li>
                                            <li className='list-group-item border-0 w-100'>
                                                <img src={cuisine?.images[0]?.urls[0]?.url} className='card-img'></img>
                                                <button className='btn btn-light rounded pos-ab' data-bs-toggle='modal' data-bs-target={`#static-backdrop-${cuisine?._id}`}><i class="bi bi-cart-plus-fill"></i></button>
                                                <CuisineModel
                                                    onClickAdd={handleAddProduct}
                                                    product={cuisine}
                                                    title={`Select number of ${cuisine.title}`}
                                                />
                                            </li>
                                        </ul>
