import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { toast } from 'react-toastify';

import { getRestaurant } from '../../Functions/Auth';

const RestaurantHome = () => {
    const location = useLocation();
    const [loading, setLoading] = useState(false);
    const [restaurant, setRestaurant] = useState('');
    const [cuisines, setCuisines] = useState('');

    const handleRoute = (option) => {
        let routes = option.split('-');
        return `${routes.length > 1 ? routes.map(route => route[0].toUpperCase().concat(route.slice(1))).join(' ') : routes[0][0].toUpperCase().concat(routes[0].slice(1))}`;
    }

    useEffect(() => {
        setLoading(true);
        getRestaurant(handleRoute(location.pathname.split('/')[2]))
            .then(res => {
                if (res.status === 200) {
                    setRestaurant(res.data.restaurant);
                    setCuisines(res.data.cuisines);
                    toast.success(res.data.message);
                } else {
                    toast.error(res.data.message)
                }
                setLoading(false);
            })
            .catch(error => {
                toast.error(error);
                setLoading(false);
            })
    }, [])

    return (
        <div className='container-fluid my-5 px-md-5'>
            <div className='row mt-5 pt-5 mx-md-2'>
                <div className='col-lg-10 col-10 offset-lg-1 offset-1 p-md-4 p-3 text-center shadow'>
                    {loading ? <h3>Loading...</h3> : <div className='row d-flex justify-content-center'>
                        <div className='col-5'>
                            <img src={restaurant?.restaurantImages?.length > 0 && restaurant?.restaurantImages[0]?.urls[0]?.url} className='card' />
                        </div>
                        <div className='col-5 text-start'>
                            <ul className='list-group'>
                                <li className='list-group-item border-0 fs-3'>{restaurant?.restaurantName}</li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Establishment Type:</label>
                                    <span className='ps-2'>
                                        {restaurant?.establishmentType}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Outlet Type:</label>
                                    <span className='ps-2'>
                                        {restaurant?.outletType?.join(', ')}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Cuisines:</label>
                                    <span className='ps-2'>
                                        {restaurant?.cuisinesType?.join(', ')}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Address:</label>
                                    <span className='ps-2'>
                                        {restaurant?.address.concat(', ', restaurant?.city)}
                                    </span>
                                </li>
                                <li className='list-group-item border-0'>
                                    <label className='text-start fw-bold fs-6'>Time:</label>
                                    <span className='ps-2'>
                                        {restaurant?.opensAt.concat(' - ', restaurant?.closesAt)}
                                    </span>
                                </li>
                            </ul>
                        </div>
                        {/* <div className='col-12 border-top border-1 text-center my-3 pt-3'>
                            <h6 className='text-start'>Full Menu</h6>
                            <ul className='list-group d-flex flex-row'>
                                {cuisines?.map(cuisine => <li className='list-group-item text-start w-25 border-0 rounded mx-1 bg-light'>{cuisine}</li>)}
                            </ul>
                        </div> */}
                    </div>}
                </div>
            </div>
        </div>
    )
}

export default RestaurantHome;
