import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useLocation } from 'react-router-dom';

const UserView = ({ title }) => {
    const { users, user, restaurant } = useSelector(state => ({ ...state }));
    const [userDet, setUserDet] = useState('');
    const location = useLocation();

    useEffect(() => {
        let params = location.pathname.split('/');
        if (params[2]) {
            setUserDet(users?.filter(user => user._id === params[2])[0]);
        } else {
            setUserDet(user || restaurant);
        }
    }, [users, location.pathname])

    return (
        <div className='container-fluid my-5 px-md-5'>
            <div className='row mt-5 pt-5 mx-md-2'>
                <div className='col-lg-8 col-10 offset-lg-2 offset-1 p-md-4 p-3 text-center shadow'>
                    <h3 className='text-center'>{title}</h3>
                    <div className='row p-5'>
                        <div className='col-lg-6 col-12 text-start'>
                            <label className='col-form-label text-start fw-bold fs-6'>First Name:</label>
                            <span className='ps-2'>
                                {userDet?.firstName}
                            </span>
                        </div>
                        <div className='col-lg-6 col-12 text-start'>
                            <label className='col-form-label text-start fw-bold fs-6'>Last Name:</label>
                            <span className='ps-2'>
                                {userDet?.lastName}
                            </span>
                        </div>
                        <div className='col-lg-6 col-12 text-start'>
                            <label className='col-form-label text-start fw-bold fs-6'>Contact:</label>
                            <span className='ps-2'>
                                {userDet?.contact}
                            </span>
                        </div>
                        <div className='col-lg-6 col-12 text-start'>
                            <label className='col-form-label text-start fw-bold fs-6'>Email:</label>
                            <span className='ps-2'>
                                {userDet?.email}
                            </span>
                        </div>
                        <div className='col-lg-6 col-12 text-start'>
                            <label className='col-form-label text-start fw-bold fs-6'>Date of Birth:</label>
                            <span className='ps-2'>
                                {userDet?.dob}
                            </span>
                        </div>
                        <div className='col-lg-6 col-12 text-start'>
                            <label className='col-form-label text-start fw-bold fs-6'>Role:</label>
                            <span className='ps-2'>
                                {userDet?.role}
                            </span>
                        </div>
                        <div className='col-lg-6 col-12 text-start'>
                            <label className='col-form-label text-start fw-bold fs-6'>Address:</label>
                            <span className='ps-2'>
                                {userDet?.address || userDet?.restaurantAddress}, {userDet?.city}, {userDet?.state}, {userDet?.zipCode}
                            </span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default UserView;
