import React, { useEffect, useState } from 'react';

const CuisineModel = ({ btnTitle, title, product, count, selection, setCount, setSelection, onClickEvent, onClickCancel }) => {
    let range = [];
    const [productQuantity, setProductQuantity] = useState('');

    useEffect(() => {
        if (product && product.quantity) {
            createRange(product && product.quantity);
        }
    }, [product])

    const createRange = (end) => {
        range = [];
        for (let i = 0; i <= end; i++) {
            range.push(i);
        }
        setProductQuantity(range);
    }

    const handleSelection = (val) => {
        setSelection(val);
        setCount(val);
    }

    const handleClick = () => {
        onClickEvent({ product, count: selection });
    }

    const handleCancel = () => {
        onClickCancel(product._id);
    }

    return (
        <div className='modal fade' id={`static-backdrop-${product._id}`} data-bs-backdrop='static' data-bs-keyboard='false' tabIndex='-1' aria-labelledby='staticBackdropLabel' aria-hidden='true'>
            <div className='modal-dialog modal-dialog-centered'>
                <div className='modal-content'>
                    <div className='modal-header'>
                        <button type='button' className='btn-close' data-bs-dismiss='modal' aria-label='Close'></button>
                    </div>
                    <div className='modal-body d-flex flex-column'>
                        <label htmlFor={`quantity-dropdown-${product._id}`} className='col-form-label fs-4'>{product?.title}</label>
                        <p>{product?.description}</p>
                        <img className='my-3' src={product?.images[0]?.urls[0]?.url} />
                        {/* <div id='carouselExampleFade' className='carousel slide carousel-fade'>
                            <div className='carousel-inner'>
                                {
                                    product?.images[0]?.urls?.map(image => <div className='carousel-item active'>
                                        <img src={image.url} className='card-img' alt='...' />
                                    </div>)
                                }
                            </div>
                            <button className='carousel-control-prev' type='button' data-bs-target='#carouselExampleFade' data-bs-slide='prev'>
                                <span className='carousel-control-prev-icon' aria-hidden='true'></span>
                                <span className='visually-hidden'>Previous</span>
                            </button>
                            <button className='carousel-control-next' type='button' data-bs-target='#carouselExampleFade' data-bs-slide='next'>
                                <span className='carousel-control-next-icon' aria-hidden='true'></span>
                                <span className='visually-hidden'>Next</span>
                            </button>
                        </div> */}
                        <div className='input-group w-50'>
                            <select
                                className='form-select rounded'
                                id={`quantity-dropdown-${product._id}`}
                                aria-label='Default select example'
                                value={selection}
                                onChange={(e) => handleSelection(e.target.value)}
                            >
                                {productQuantity && productQuantity.map(q => <option key={q} value={q}>{q}</option>)}
                            </select>
                            <span className='input-group-text border-0 bg-white m-0 px-1'>&#xd7;</span>
                            <span className='input-group-text border-0 bg-white m-0 px-1'>&#36;{product.price}</span>
                            <span className='input-group-text border-0 bg-white m-0 px-1'>&#61;</span>
                            <span className='input-group-text border-0 bg-white m-0 px-1'>&#36;{count * product.price}</span>
                        </div>
                        <div className='my-2'>
                            <label for='exampleFormControlTextarea1' className='form-label'>Instructions</label>
                            <textarea className='form-control' id='exampleFormControlTextarea1' rows='3'></textarea>
                        </div>
                    </div>
                    <div className='modal-footer jsutify-content-center'>
                        <button className='btn btn-filled' data-bs-dismiss='modal' aria-label='Close' onClick={handleCancel}>Cancel</button>
                        <button className='btn btn-hollow' data-bs-dismiss='modal' disabled={selection == 0} onClick={handleClick}>{btnTitle}</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CuisineModel;
