import React, { useEffect, useState } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { onAuthStateChanged } from 'firebase/auth';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import './App.css';
import { auth } from './firebase';
import { getUsers, getRestaurants, currentUser, currentRestaurant, getCart, getOrders } from './Functions/Auth';
import Nav from './Components/Nav';
import Home from './Components/Home';
import Login from './Pages/User/Login';
import Register from './Pages/User/Register';
import RegisterComplete from './Pages/User/RegisterComplete';
import ResetPassword from './Pages/User/ResetPassword';
import SignUp from './Pages/Partner/Signup';
import SignUpType from './Pages/Partner/SignupType';
import SignUpImages from './Pages/Partner/SignupImages';
import PartnerDashboard from './Pages/Partner/PartnerDashboard';
import AdminDashboard from './Pages/Admin/AdminDashboard';
import ManageRestaurants from './Pages/Admin/ManageRestaurants';
import ManageUsers from './Pages/Admin/ManageUsers';
import UserView from './Pages/Admin/UserView';
import CreateCuisine from './Pages/Partner/CreateCuisine';
import ManageCuisines from './Pages/Partner/ManageCuisines';
import ManageOrders from './Pages/Partner/ManageOrders';
import OrdersHistory from './Pages/Partner/OrderHistory';
import RestaurantsHome from './Pages/User/RestaurantsHome';
import RestaurantHome from './Pages/User/RestaurantHome';
import Cart from './Pages/User/Cart';

const App = () => {
	const dispatch = useDispatch();
	const navigate = useNavigate();
	const location = useLocation();
	const [navObj, setNavObj] = useState({});

	useEffect(() => {
		if (location.pathname.includes('partner')) {
			setNavObj({
				title: 'Flavor Chronicles for business',
				buttonOne: 'Login to view your existing restaurants',
				buttonTwo: 'Register your restaurant',
				buttonOneRoute: 'partner-login',
				buttonTwoRoute: 'partner-create-account',
				positon: 'fixed-bottom'
			})
		} else {
			setNavObj({
				title: 'Flavor Chronicles',
				buttonOne: 'Login',
				buttonTwo: 'Create Account'
			})
		}
	}, [location.pathname])

	useEffect(() => {
		onAuthStateChanged(auth, async (user) => {
			if (user) {
				const idTokenResult = await user.getIdTokenResult();
				let options = [];
				let uaoptions = [];
				if (location.pathname.includes('partner')) {
					currentRestaurant(user?.email, idTokenResult.token)
						.then((res) => {
							if (res.status === 200) {
								const { _id, restaurantName, address, state, city, zipCode, contact, ownerContact, firstName, lastName, email, establishmentType, outletType, cuisinesType, openDays, opensAt, closesAt, menuImages, restaurantImages, foodImages, status } = res.data.restaurant;
								const { idToken } = res.config.headers;
								options = ['Dashboard', 'Create Cuisine', 'Manage Cuisines', 'Manage Orders', 'Orders History', 'Manage Profile'];
								dispatch({
									type: 'LOGGED_IN_RESTAURANT',
									payload: { _id, restaurantName, address, state, city, zipCode, contact, ownerContact, firstName, lastName, email, establishmentType, outletType, cuisinesType, openDays, opensAt, closesAt, menuImages, restaurantImages, foodImages, status, options, token: idToken }
								});
							} else {
								toast.error(res.data.message);
							}
						})
						.catch(() => toast.error('Something went wromg...please try again'))
				} else {
					console.log('Check-->hello');
					currentUser(user.email, idTokenResult.token)
						.then((res) => {
							if (res.status === 200) {
								const { firstName, lastName, dob, gender, email, contact, address, state, city, zipCode, role, _id } = res.data.user;
								const { idToken } = res.config.headers;
								switch (role) {
									case 'admin': options = ['Dashboard', 'Add Restaurant', 'Manage Restaurants', 'Manage Users', 'Manage Profile'];
										break;
									case 'crew': options = ['Dashboard', 'Current Order', 'Orders History', 'Manage Profile'];
										break;
									default: options = ['Dashboard', 'Current Order', 'Orders History', 'Manage Profile'];
										break;
								};
								dispatch({
									type: 'LOGGED_IN_USER',
									payload: { firstName, lastName, dob, gender, email, contact, address, state, city, zipCode, role, _id, options, uaoptions, token: idToken }
								});

								getCart(email, idTokenResult.token)
									.then(res => {
										if (res.status === 200) {
											dispatch({
												type: 'CART',
												payload: res.data.cart
											});
										}
									})
									.catch((error) => toast.error(error))

								if (role === 'crew') {
									navigate('/crew-dashboard');
								}
								else if (role === 'admin') {
									getRestaurants(idTokenResult.token)
										.then(res => {
											if (res.status === 200) {
												dispatch({
													type: 'REGISTERED_RESTAURANTS',
													payload: res.data.restaurants
												})
											}
										})
									getUsers(idTokenResult.token)
										.then((res) => {
											if (res.status === 200) {
												dispatch({
													type: 'REGISTERED_USERS',
													payload: res.data.users
												})
											}
										})
										.catch((error) => toast.error(error))
								}
								else {
									navigate('/');
								}
							} else {
								toast.error(res.data.message);
							}
						})
						.catch((error) => toast.error(error))
				}
			}
			else {
				dispatch({
					type: 'LOGOUT',
					payload: null
				})
			}
		})
	}, [dispatch]);

	return (
		<div className='App'>
			{Object.keys(navObj).length && <Nav
				obj={navObj}
			/>}
			<ToastContainer />
			<Routes>
				<Route exact path='/' element={<Home />} />
				<Route exact path='/login' element={<Login />} />
				<Route exact path='/create-account' element={<Register redirectUrl={process.env.REACT_APP_REGISTER_REDIRECT_URL} />} />
				<Route exact path='/register-complete' element={<RegisterComplete />} />
				<Route exact path='/reset-password' element={<ResetPassword />} />
				<Route exact path='/partner-create-account' element={<Register redirectUrl={process.env.REACT_APP_PARTNER_REGISTER_REDIRECT_URL} />} />
				<Route exact path='/partner-signup' element={<SignUp emailDisabled={true} />} />
				<Route exact path='/partner-signup/restaurant-type' element={<SignUpType />} />
				<Route exact path='/partner-signup/upload-images' element={<SignUpImages />} />
				<Route exact path='/partner-login' element={<Login title={'Partner Login'} />} />
				<Route exact path='/partner-dashboard' element={<PartnerDashboard />} />
				<Route exact path='/partner-create-cuisine' element={<CreateCuisine />} />
				<Route exact path='/partner-update-cuisine/:slug' element={<CreateCuisine />} />
				<Route exact path='/partner-manage-cuisines' element={<ManageCuisines />} />
				<Route exact path='/partner-manage-orders' element={<ManageOrders />} />
				<Route exact path='/partner-orders-history' element={<OrdersHistory />} />
				<Route exact path='/partner-manage-profile' element={<PartnerDashboard />} />
				<Route exact path='/admin-dashboard' element={<AdminDashboard />} />
				<Route exact path='/admin-add-restaurant' element={<SignUp emailDisabled={false} />} />
				<Route exact path='/admin-add-restaurant/restaurant-type' element={<SignUpType />} />
				<Route exact path='/admin-add-restaurant/upload-images' element={<SignUpImages />} />
				<Route exact path='/admin-restaurant-view/:slug' element={<PartnerDashboard />} />
				<Route exact path='/admin-manage-restaurants' element={<ManageRestaurants />} />
				<Route exact path='/admin-manage-users' element={<ManageUsers />} />
				<Route exact path='/admin-user-view/:slug' element={<UserView title={'Manage User'} />} />
				<Route exact path='/admin-manage-profile' element={<UserView title={'Manage Profile'} />} />
				<Route exact path='/zip/:slug' element={<RestaurantsHome />} />
				<Route exact path='/store/:slug' element={<RestaurantHome />} />
				<Route exact path='/cart' element={<Cart />} />
			</Routes>
		</div>
	);
}

export default App;
