import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

import './Auth.css';

import { getRestaurantsByZip } from '../../Functions/Auth';

const RestaurantsHome = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [active, setActive] = useState('Delivery');
    const [restaurants, setRestaurants] = useState('');

    useEffect(() => {
        setLoading(true);
        getRestaurantsByZip(location.pathname.split('/')[2])
            .then(res => {
                if (res.status === 200) {
                    setRestaurants(res.data.restaurants);
                    toast.success(res.message);
                } else {
                    toast.error(res.message)
                }
                setLoading(false);
            })
            .catch(error => {
                toast.error(error);
                setLoading(false);
            })
    }, [])

    const handleRoute = (option) => {
        let routes = option.split(' ');
        return `/${routes.length > 1 ? routes.map(route => route.toLowerCase()).join('-') : routes[0].toLowerCase()}`;
    }

    const handleRestaurant = (str) => {
        navigate(`/store${handleRoute(str)}`)
    }

    let outletArray = ['Bakery', 'Beverage Shop', 'Café', 'Casual Dining', 'Club', 'Cocktail Bar'];
    let categoryArray = ['Afghan', 'African', 'American', 'Andhra', 'Arabian', 'Armenian'];

    return (
        <div className='container-fluid my-5 px-md-5'>
            <div className='row mt-5 pt-5 mx-md-2'>
                <div className='col-lg-10 col-10 offset-lg-1 offset-1 p-md-4 p-3 shadow'>
                    <div class='row mb-3'>
                        <div className='col-lg-2 col-md-5 col-8 p-0 mx-2 mx-md-0'>
                            <div className='border border-dark rounded-pill d-flex flex-row'>
                                <button id='Delivery' className={`btn btn-raised rounded-pill w-100 searchOption fs-6 ${active === 'Delivery' && 'btn-switch-filled'}`} onClick={(e) => { setActive(e.target.id) }}>
                                    Delivery
                                </button>
                                <button id='Pickup' className={`btn btn-raised rounded-pill w-100 searchOption fs-6 ${active === 'Pickup' && 'btn-switch-filled'}`} onClick={(e) => { setActive(e.target.id) }}>
                                    Pickup
                                </button>
                            </div>
                        </div>
                    </div>
                    <div className='col-12 text-center my-3 pt-3'>
                        <h3 className='text-start'>{`Food ${active} in ${location.pathname.split('/')[2]}`}</h3>
                        <p className='text-p'>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</p>
                    </div>
                    <div className='col-12 border-top border-1 text-center my-3 pt-3'>
                        <h3 className='text-start'>Explore by category</h3>
                        <ul className='list-group d-flex flex-row'>
                            {categoryArray?.map(category => <li className='list-group-item text-start w-25 border-0 rounded mx-1 bg-light'>{category}</li>)}
                        </ul>
                    </div>
                    <div className='col-12 border-top border-1 text-center my-3 pt-3'>
                        <h3 className='text-start'>Explore by outlet</h3>
                        <ul className='list-group d-flex flex-row'>
                            {outletArray?.map(outlet => <li className='list-group-item text-start w-25 border-0 rounded mx-1 bg-light'>{outlet}</li>)}
                        </ul>
                    </div>
                    <div className='col-12 border-top border-1 text-center my-3 pt-3'>
                        <div className='row d-flex m-1'>
                            {/* {
                                (!loading && restaurants.length > 0) ? restaurants.map(restaurant => <div key={restaurant.restaurantName} className='col-lg-6 d-flex flex-row p-2 cursor-pointer' onClick={() => handleRestaurant(restaurant.restaurantName)}>
                                    <img src={restaurant?.restaurantImages[0]?.urls[0]?.url} className='restaurantImage px-1' />
                                    <ul className='list-group w-100'>
                                        <li className='list-group-item py-0 m-0 border-0 bg-transparent text-start d-flex justify-content-between'>
                                            <h4>{restaurant?.restaurantName}</h4>
                                            <p>{restaurant?.rating || 4.5}</p>
                                        </li>
                                        <li className='list-group-item py-0 border-0 bg-transparent text-start'>{restaurant?.cuisinesType.join(', ') || 'description'}</li>
                                        <li className='list-group-item py-0 border-0 bg-transparent text-start'>{restaurant?.address.concat(', ', restaurant?.city) || 'address'}</li>
                                    </ul>
                                </div>) : <h3 className='text-center'>No restaurants at this location</h3>
                            } */}
                            {
                                (!loading && restaurants.length > 0) ? restaurants.map(restaurant => <div key={restaurant?._id} className='col-6 my-2'>
                                    <ul className='list-group text-start d-flex flex-row border border-1'>
                                        <li className='list-group-item border-0 w-100 p-2'>
                                            <img src={restaurant?.restaurantImages[0]?.urls[0]?.url} className='card-img'></img>
                                        </li>
                                        <li className='list-group-item border-0 w-100 p-3'>
                                            <ul className='list-group'>
                                                <li className='list-group-item border-0 fs-6 d-flex justify-content-between p-0'>
                                                    <h4>{restaurant?.restaurantName}</h4>
                                                    <p>{restaurant?.rating || 4.5}</p>
                                                </li>
                                                <li className='list-group-item border-0 p-0'>
                                                    {restaurant?.cuisinesType.join(', ') || 'description'}
                                                </li>
                                                <li className='list-group-item border-0 p-0'>
                                                    {restaurant?.address.concat(', ', restaurant?.city) || 'address'}
                                                </li>
                                            </ul>
                                        </li>
                                    </ul>
                                </div>) : ''
                            }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default RestaurantsHome;
